using System.Reflection;

namespace Multimanager
{
    public class AppInfo
    {
        public string name = Assembly.GetExecutingAssembly().GetName().Name;
        public string description = "Contains system customization options such as a theme manager (works on non-activated windows 10 platforms) and display resolution changer, as well as a task manager and a console.";
        public bool showInOfflineMode = true;
        public string icon = "";
        public string version = Assembly.GetExecutingAssembly().GetName().Version.ToString();
    }
}
